INSERT INTO `questoes` VALUES (734,NULL,NULL,'RES','20','','','1º','','',NULL,'','6','1','5','150','Sobre o capacete é correto afirmar:','diferentes tipos de capacete oferecem diferentes graus de proteção.','é muito importante que o capacete tenha sido aprovado pelo INMETRO.','deve ficar bem ajustado na cabeça.','deve ser substituído sempre que passar da validade.','todas as alternativas estão corretas.','resp5','S','N','N','N','resp4','(null)','(null)','Livre',NULL,NULL,NULL);
