INSERT INTO `questoes` VALUES (736,NULL,NULL,'','','','','','','',NULL,'','6','1','1','139','A principal função da bateria é:','manter a bobina ou ignição eletrônica aquecidas.','fornecer energia para luzes e auto-rádio.','ajustar o motor em relação aos pólos positivo e negativo.','ajustar o motor em relação aos pólos magnéticos norte e sul.','receber e acumular energia para acionar o motor de partida.','resp5','S','N','N','N','resp4','(null)','(null)','Livre',NULL,NULL,NULL);
